mapper.select:
select
from t_outrecord where outReason like CONCAT(CONCAT('%', #{outreason}), '%') or userName like CONCAT(CONCAT('%', #{username}), '%')
ORDER BY outTime DESC

select
from t_outrecord where 1=1
and orgId = #{condition,jdbcType=VARCHAR}
and openId = #{condition,jdbcType=VARCHAR}
and (outReason like CONCAT(CONCAT('%', #{outreason}), '%') or userName like CONCAT(CONCAT('%', #{outreason}), '%')) ORDER BY state ASC, outTime DESC
